import{_ as a,c as n,o as s,e}from"./app.6afe3b59.js";var p="/blog/assets/2022-12-26-10-08-23.8ba9b555.png",o="/blog/assets/2022-12-26-10-11-40.91b66334.png",l="/blog/assets/2022-12-26-10-33-14.40167d02.png";const g=JSON.parse('{"title":"\u4EBA\u5DE5\u667A\u80FD\u5B66\u4E60\u8BB0\u5F55","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u5B66\u4E60\u73AF\u5883\u51C6\u5907","slug":"\u5B66\u4E60\u73AF\u5883\u51C6\u5907"},{"level":3,"title":"\u5B89\u88C5 Anaconda","slug":"\u5B89\u88C5-anaconda"},{"level":3,"title":"\u5B89\u88C5 PyTorch","slug":"\u5B89\u88C5-pytorch"},{"level":2,"title":"\u77E5\u8BC6\u4E86\u89E3","slug":"\u77E5\u8BC6\u4E86\u89E3"},{"level":3,"title":"\u4EC0\u4E48\u662Ftensorflow","slug":"\u4EC0\u4E48\u662Ftensorflow"},{"level":3,"title":"NumPy","slug":"numpy"},{"level":3,"title":"PyTorch","slug":"pytorch"}],"relativePath":"guide/ai/index.md"}'),t={name:"guide/ai/index.md"},r=e(`<h1 id="\u4EBA\u5DE5\u667A\u80FD\u5B66\u4E60\u8BB0\u5F55" tabindex="-1">\u4EBA\u5DE5\u667A\u80FD\u5B66\u4E60\u8BB0\u5F55 <a class="header-anchor" href="#\u4EBA\u5DE5\u667A\u80FD\u5B66\u4E60\u8BB0\u5F55" aria-hidden="true">#</a></h1><h2 id="\u5B66\u4E60\u73AF\u5883\u51C6\u5907" tabindex="-1">\u5B66\u4E60\u73AF\u5883\u51C6\u5907 <a class="header-anchor" href="#\u5B66\u4E60\u73AF\u5883\u51C6\u5907" aria-hidden="true">#</a></h2><p>\u5DF2\u5B58\u5728\u73AF\u5883\uFF1A</p><pre><code>* WSL2 Ubuntu 20.04
* Python 3.8.10
</code></pre><h3 id="\u5B89\u88C5-anaconda" tabindex="-1">\u5B89\u88C5 Anaconda <a class="header-anchor" href="#\u5B89\u88C5-anaconda" aria-hidden="true">#</a></h3><p>Anaconda \u662F\u4E00\u4E2A\u5F00\u6E90\u7684 Python \u53D1\u884C\u7248\uFF0C\u7528\u4E8E\u79D1\u5B66\u8BA1\u7B97\uFF0C\u5305\u542B\u4E86 conda\u3001Python \u7B49 1,500 \u591A\u4E2A\u79D1\u5B66\u5305\u53CA\u5176\u4F9D\u8D56\u9879\u3002Anaconda \u53EF\u4EE5\u5728 Linux\u3001macOS \u548C Windows \u4E0A\u8FD0\u884C\u3002</p><p>linux\u5B89\u88C5\u5B98\u7F51\uFF1A<a href="https://docs.continuum.io/anaconda/install/linux/" target="_blank" rel="noopener noreferrer">https://docs.continuum.io/anaconda/install/linux/</a></p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u4E0B\u8F7D\u5B89\u88C5\u5305</span></span>
<span class="line"><span style="color:#A6ACCD;">wget https://repo.anaconda.com/archive/Anaconda3-2021.05-Linux-x86_64.sh</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u5B89\u88C5</span></span>
<span class="line"><span style="color:#A6ACCD;">bash Anaconda3-2021.05-Linux-x86_64.sh</span></span>
<span class="line"></span></code></pre></div><p>\u5B89\u88C5\u5B8C\u6210\u540E\uFF0C\u6700\u540E\u4E00\u4E2A\u6B65\u9AA4\u4F1A\u95EE\u4F60\u8981\u4E0D\u8981\u5C06 Anaconda \u7684\u8DEF\u5F84\u6DFB\u52A0\u5230 PATH \u4E2D\uFF0C\u63A8\u8350\u9009\u62E9 yes\u3002</p><p>\u5982\u679C\u9009\u62E9\u4E86no\uFF0C\u90A3\u4E48\u9700\u8981\u624B\u52A8\u6DFB\u52A0\u73AF\u5883\u53D8\u91CF\uFF1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u6253\u5F00\u914D\u7F6E\u6587\u4EF6</span></span>
<span class="line"><span style="color:#A6ACCD;">vim </span><span style="color:#89DDFF;">~</span><span style="color:#A6ACCD;">/.bashrc</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u6DFB\u52A0\u73AF\u5883\u53D8\u91CF</span></span>
<span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> PATH=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">/home/username/anaconda3/bin:</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">PATH</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p>\u6216\u8005\u901A\u8FC7\u547D\u4EE4\u884C\u6DFB\u52A0\uFF1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u8FDB\u5165\u5230anaconda\u5B89\u88C5\u76EE\u5F55</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> /home/username/anaconda3/bin</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u6DFB\u52A0\u73AF\u5883\u53D8\u91CF</span></span>
<span class="line"><span style="color:#82AAFF;">source</span><span style="color:#A6ACCD;"> activate</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># init</span></span>
<span class="line"><span style="color:#A6ACCD;">conda init</span></span>
<span class="line"></span></code></pre></div><p>\u6B64\u65F6\u6211\u4EEC\u67E5\u770Bpython3\u7684\u4E0D\u540C\uFF0C\u591A\u4E86\u4E2A<code>\uFF1A\uFF1AAnaconda</code>\uFF1A</p><p><img src="`+p+`" alt=""></p><p>\u68C0\u67E5\u662F\u5426\u5B89\u88C5\u6210\u529F\uFF1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u67E5\u770B\u7248\u672C</span></span>
<span class="line"><span style="color:#A6ACCD;">conda -V</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u542F\u52A8jupyter</span></span>
<span class="line"><span style="color:#A6ACCD;">jupyter notebook</span></span>
<span class="line"></span></code></pre></div><p><img src="`+o+`" alt=""></p><p>\u6D4F\u89C8\u5668\u6253\u5F00<code>http://localhost:8888</code>\uFF0C\u5373\u53EF\u770B\u5230jupyter\u7684\u754C\u9762\u3002</p><h3 id="\u5B89\u88C5-pytorch" tabindex="-1">\u5B89\u88C5 PyTorch <a class="header-anchor" href="#\u5B89\u88C5-pytorch" aria-hidden="true">#</a></h3><p>PyTorch \u662F\u4E00\u4E2A\u57FA\u4E8E Python \u7684\u79D1\u5B66\u8BA1\u7B97\u5305\uFF0C\u4E3B\u8981\u7528\u4E8E\u4E24\u4E2A\u9886\u57DF\uFF1A\u4E00\u662F\u4F5C\u4E3A NumPy \u7684\u66FF\u4EE3\u54C1\uFF0C\u53EF\u4EE5\u4F7F\u7528GPU\u52A0\u901F\u8BA1\u7B97\uFF1B\u4E8C\u662F\u4F5C\u4E3A\u6DF1\u5EA6\u5B66\u4E60\u5E73\u53F0\uFF0C\u63D0\u4F9B\u4E86\u6700\u5927\u7684\u7075\u6D3B\u6027\u548C\u901F\u5EA6\u3002</p><p>\u5B89\u88C5\u5B98\u7F51\uFF1A<a href="https://pytorch.org/get-started/locally/" target="_blank" rel="noopener noreferrer">https://pytorch.org/get-started/locally/</a></p><p>\u6309\u7167\u5B98\u7F51\u548C\u672C\u5730\u73AF\u5883\uFF0C\u5148\u62E9\u5BF9\u5E94\u7684\u4E0D\u540C\u7684\u5B89\u88C5\u547D\u4EE4\uFF0C\u672C\u793A\u4F8B\u9009\u62E9\u7684\u662F<code>stable(1.3.1)</code>\u3001<code>Linux</code>\u3001<code>Conda</code>\u3001<code>Python</code>\u3001 <code>CPU</code>:</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u5B89\u88C5</span></span>
<span class="line"><span style="color:#A6ACCD;">conda install pytorch torchvision torchaudio cpuonly -c pytorch</span></span>
<span class="line"></span></code></pre></div><p>\u5B89\u88C5\u5B8C\u6210\u540E\uFF0C\u68C0\u67E5\u662F\u5426\u5B89\u88C5\u6210\u529F\uFF1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u67E5\u770B\u7248\u672C</span></span>
<span class="line"><span style="color:#A6ACCD;">python -c </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">import torch; print(torch.__version__)</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p>\u6216\u8005\u901A\u8FC7\u9A8C\u8BC1\u662F\u5426\u80FD\u5BFC\u5165torch\u6765\u9A8C\u8BC1\uFF1A</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u8FDB\u5165python3</span></span>
<span class="line"><span style="color:#A6ACCD;">python3</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u5BFC\u5165torch\u5E76\u8FDB\u884C\u4E00\u4E2A\u8FD0\u7B97</span></span>
<span class="line"><span style="color:#A6ACCD;">import torch</span></span>
<span class="line"><span style="color:#A6ACCD;">x = torch.empty</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">5, 3</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">print</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">x</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre></div><p>\u8F93\u51FA\uFF1A <img src="`+l+'" alt=""></p><h2 id="\u77E5\u8BC6\u4E86\u89E3" tabindex="-1">\u77E5\u8BC6\u4E86\u89E3 <a class="header-anchor" href="#\u77E5\u8BC6\u4E86\u89E3" aria-hidden="true">#</a></h2><h3 id="\u4EC0\u4E48\u662Ftensorflow" tabindex="-1">\u4EC0\u4E48\u662Ftensorflow <a class="header-anchor" href="#\u4EC0\u4E48\u662Ftensorflow" aria-hidden="true">#</a></h3><p>TensorFlow \u662F\u4E00\u4E2A\u5F00\u6E90\u7684\u673A\u5668\u5B66\u4E60\u5E93\uFF0C\u7531 Google \u5F00\u53D1\uFF0C\u7528\u4E8E\u673A\u5668\u5B66\u4E60\u548C\u6DF1\u5EA6\u5B66\u4E60\u7B49\u9886\u57DF\u3002TensorFlow \u63D0\u4F9B\u4E86 Python \u548C C++ \u4E24\u79CD\u7F16\u7A0B\u8BED\u8A00\u7684 API\uFF0C\u652F\u6301 Linux\u3001macOS \u548C Windows \u7B49\u591A\u79CD\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><p>tensorflow\u4E2D\u6587\u5B98\u7F51\uFF1A<a href="https://tensorflow.google.cn/%E3%80%82" target="_blank" rel="noopener noreferrer">https://tensorflow.google.cn/\u3002</a></p><p>\u7531\u4E8Etensorflow\u76F8\u5173\u57FA\u7840\u77E5\u8BC6\u8F83\u591A\uFF0C\u8FD9\u91CC\u53EA\u662F\u7B80\u5355\u4ECB\u7ECD\u4E00\u4E0B\uFF0C\u8D34\u4E0A\u4EE5\u4E0B\u77E5\u4E4E\u6587\u7AE0\u6709\u5229\u4E8E\u5927\u5BB6\u7684\u7406\u89E3\uFF1A</p><p><a href="https://zhuanlan.zhihu.com/p/59077525" target="_blank" rel="noopener noreferrer">\u4EC0\u4E48\u662Ftensorflow</a></p><h3 id="numpy" tabindex="-1">NumPy <a class="header-anchor" href="#numpy" aria-hidden="true">#</a></h3><p>NumPy \u662F\u4E00\u4E2A Python \u7684\u5F00\u6E90\u6269\u5C55\u7A0B\u5E8F\u5E93\uFF0C\u652F\u6301\u9AD8\u6027\u80FD\u7684\u591A\u7EF4\u6570\u7EC4\u5BF9\u8C61\uFF0C\u4EE5\u53CA\u7528\u4E8E\u5904\u7406\u6570\u7EC4\u7684\u5404\u79CD\u51FD\u6570\u3002NumPy \u7684\u4E3B\u8981\u5BF9\u8C61\u662F\u540C\u6784\u591A\u7EF4\u6570\u7EC4\u3002\u5B83\u662F\u4E00\u4E2A\u5143\u7D20\u8868\uFF08\u901A\u5E38\u662F\u6570\u5B57\uFF09\uFF0C\u6240\u6709\u7C7B\u578B\u90FD\u76F8\u540C\uFF0C\u7531\u975E\u8D1F\u6574\u6570\u5143\u7EC4\u7D22\u5F15\u3002\u5728 NumPy \u7EF4\u5EA6\u4E2D\u79F0\u4E3A\u8F74\u3002\u6570\u7EC4\u7684\u6570\u5B57\u79F0\u4E3A\u5143\u7D20\u6216\u5143\u7D20\u3002NumPy \u7684\u6570\u7EC4\u7C7B\u88AB\u79F0\u4E3A ndarray\u3002\u5B83\u4E5F\u88AB\u79F0\u4E3A\u522B\u540D\u6570\u7EC4\u3002</p><p>NumPy \u7684\u4E3B\u8981\u76EE\u7684\u4E4B\u4E00\u662F\u7528\u4E8E\u6570\u7EC4\u8BA1\u7B97\u3002\u5B83\u63D0\u4F9B\u4E86\u7EBF\u6027\u4EE3\u6570\u548C\u968F\u673A\u6570\u751F\u6210\u7684\u529F\u80FD\u3002NumPy \u53EF\u4EE5\u4E0E\u5176\u4ED6\u5E93\u96C6\u6210\uFF0C\u4F8B\u5982 Matplotlib\uFF08\u7ED8\u56FE\u5E93\uFF09\u3001OpenCV\uFF08\u56FE\u50CF\u5904\u7406\u5E93\uFF09\u7B49\u3002</p><p>NumPy \u5B98\u7F51\uFF1A<a href="https://numpy.org/" target="_blank" rel="noopener noreferrer">https://numpy.org/</a></p><h3 id="pytorch" tabindex="-1">PyTorch <a class="header-anchor" href="#pytorch" aria-hidden="true">#</a></h3><p>PyTorch \u662F\u4E00\u4E2A\u57FA\u4E8E Python \u7684\u79D1\u5B66\u8BA1\u7B97\u5305\uFF0C\u4E3B\u8981\u7528\u4E8E\u4E24\u4E2A\u9886\u57DF\uFF1A\u4E00\u662F\u4F5C\u4E3A NumPy \u7684\u66FF\u4EE3\u54C1\uFF0C\u53EF\u4EE5\u4F7F\u7528GPU\u52A0\u901F\u8BA1\u7B97\uFF1B\u4E8C\u662F\u4F5C\u4E3A\u6DF1\u5EA6\u5B66\u4E60\u5E73\u53F0\uFF0C\u63D0\u4F9B\u4E86\u6700\u5927\u7684\u7075\u6D3B\u6027\u548C\u901F\u5EA6\u3002</p><p>PyTorch \u5B98\u7F51\uFF1A<a href="https://pytorch.org/" target="_blank" rel="noopener noreferrer">https://pytorch.org/</a></p><p>\u4E3A\u7406\u89E3\u548C\u7EC3\u4E60 PyTorch\uFF0C\u5728\u6B64\u8D34\u4E0A\u4E00\u7BC7\u77E5\u4E4E\u6587\u7AE0\uFF1A</p><p><a href="https://zhuanlan.zhihu.com/p/99318332#:~:text=60%E9%A2%98PyTorch%E7%AE%80%E6%98%93%E5%85%A5%E9%97%A8%E6%8C%87%E5%8D%97%EF%BC%8C%E5%81%9A%E6%8A%80%E6%9C%AF%E7%9A%84%E5%BC%84%E6%BD%AE%E5%84%BF%F0%9F%8C%8A%201%201%20%E5%88%9D%E8%AF%86PyTorch%202%201.1%20%E5%BC%A0%E9%87%8F%201.%E5%AF%BC%E5%85%A5pytorch%E5%8C%85,3%20%E7%A5%9E%E7%BB%8F%E7%BD%91%E7%BB%9C%20%E8%BF%99%E9%83%A8%E5%88%86%E4%BC%9A%E5%AE%9E%E7%8E%B0LeNet5%EF%BC%8C%E7%BB%93%E6%9E%84%E5%A6%82%E4%B8%8B%E6%89%80%E7%A4%BA%208%203.1%20%E5%AE%9A%E4%B9%89%E7%BD%91%E7%BB%9C%20...%20%E6%9B%B4%E5%A4%9A%E9%A1%B9%E7%9B%AE" target="_blank" rel="noopener noreferrer">pytorch 60\u9898</a></p><p>\u4F7F\u7528PyTorch\u642D\u5EFA\u5377\u79EF\u795E\u7ECF\u7F51\u7EDC\uFF1A<a href="https://blog.csdn.net/juhanishen/article/details/123462838" target="_blank" rel="noopener noreferrer">https://blog.csdn.net/juhanishen/article/details/123462838</a></p><p>PyTorch\u642D\u5EFACNN\u5377\u79EF\u795E\u7ECF\u7F51\u7EDC\u8BE6\u89E3\uFF1A<a href="https://blog.csdn.net/weixin_44912159/article/details/105345760" target="_blank" rel="noopener noreferrer">https://blog.csdn.net/weixin_44912159/article/details/105345760</a></p>',46),c=[r];function i(h,d,y,A,u,E){return s(),n("div",null,c)}var f=a(t,[["render",i]]);export{g as __pageData,f as default};
